content:
.template_sidebar-marginal #content {
        width: 100%;
    }

    #marginal {
        display: none;
    }

    .template_sidebar #sidebar,
    .template_sidebar-marginal #sidebar,
    .template_sidebar-special #sidebar {
        display: none;
    }

    .template_sidebar-marginal #content #primary,
    #content #primary {
        width: 99%;
        margin-left: 20px;
        margin-right: 10px;
    }

    @media only screen and (max-width: 768px) {

        .template_sidebar #content,
        .template_sidebar-marginal #content,
        .template_sidebar-special #content {
            margin: 0 0px 0 0px;
            width: 90%;
        }

        #content &amp;amp;
        gt;
        .columns &amp;amp;
        gt;
        .col_2-1_1 &amp;amp;
        gt;
        *,
        #primary &amp;amp;
        gt;
        *,
        #marginal &amp;amp;
        gt;

        * {
            margin-right: 10px;
        }
    }

.photo-story {
        position: relative;
        /* For .photo-story-index and .photo-story-zoom */
    }

    .photo-story-header {
        position: relative;
        padding: 0 0 10px;
    }

    .photo-story-index {
        float: left;
        color: #196aaa;
        font-size: 16px;
        font-weight: bold;
    }

    .photo-story-zoom {
        float: right;
        font-size: 14px;
        padding-left: 10px;
        background: url('/sysmedia/media/style/img/who_sprite.gif') no-repeat 0 -426px;
    }

    .photo-story-zoom:visited {
        color: #196aaa;
    }

    .photo-story-item-image {
        position: relative;
        /* For .photo-story-next and .photo-story-prev */
    }

    .photo-story-item-image img {
        display: block;
        width: 100%;
        height: auto;
    }

    .photo-story-prev,
    .photo-story-next {
        display: none;
        width: 60px;
        height: 100%;
        position: absolute;
        top: 0;
        left: 0;
        background: url(about:blank);
        /* IE8/9 don't make the entire area clickable */
    }

    .photo-story-next {
        left: auto;
        right: 0;
    }

    .photo-story-prev:after,
    .photo-story-next:after {
        content: '';
        display: block;
        width: 30px;
        height: 50px;
        margin: -25px 0 0 -15px;
        position: absolute;
        top: 50%;
        left: 50%;
        background: url('/sysmedia/media/style/img/photo_story_sprite.png') no-repeat -1px 0;
    }

    .photo-story-next:after {
        background-position: -62px 0;
    }

    .photo-story-prev:hover:after {
        background-position: -32px 0;
    }

    .photo-story-next:hover:after {
        background-position: -93px 0;
    }

    /* Hide the next/prev links in zoom mode */
    #sb-player .photo-story-prev,
    #sb-player .photo-story-next {
        display: none;
    }

    .owl-carousel .photo-story-item-text {
        background: #f0f0f0;
        padding-top: 7px;
    }

    .photo-story-item-text-caption {
        text-align: right;
        font-size: 12px;
        font-style: italic;
        padding-right: 10px;
    }

    .photo-story-item-text-desc {
        padding: 8px 15px 15px;
        line-height: 1.4;
    }

    .pswp__caption {
        display: none;
    }

    .pswp__custom-caption {
        position: absolute;
        left: 0;
        bottom: 0;
        width: 100%;
        color: #bbb;
        background: #000;
        background: rgba(0, 0, 0, 0.5);
        opacity: 1;
        -webkit-transition: opacity 0.2s;
        transition: opacity 0.2s;
        /* Fix the more/less button so it doesn't get hidden during animation */
        overflow: visible !important;
    }

    .pswp__custom-caption__inner {
        margin: 0 auto;
    }

    .pswp__custom-caption__less {
        background: rgba(0, 0, 0, 0.9);
    }

    .pswp__custom-caption__less:before,
    .pswp__custom-caption__more:before {
        content: '';
        position: absolute;
        left: 50%;
        top: -25px;
        margin-left: -22px;
        width: 44px;
        height: 25px;
        background-image: url('/sysmedia/media/style/css/screen/photoswipe-default-skin/default-skin.png');
        background-repeat: no-repeat;
        background-position: -181px -45px;
        background-color: #000;
        background-color: rgba(0, 0, 0, 0.5);
        opacity: 1;
        -webkit-transition: opacity 0.2s;
        transition: opacity 0.2s;
    }

    .pswp__custom-caption__less:before {
        background-position: -181px -9px;
        background-color: rgba(0, 0, 0, 0.9);
    }

    .pswp__custom-caption--idle.pswp__custom-caption__less:before,
    .pswp__custom-caption--idle.pswp__custom-caption__more:before {
        opacity: 0.001;
    }

    .pswp__custom-caption--hidden {
        opacity: 0;
    }

    .pswp__custom-caption__more:after {
        content: '';
        position: absolute;
        left: 0;
        bottom: 0;
        width: 100%;
        height: 30px;
        background: -webkit-linear-gradient(top, rgba(255, 255, 255, 0) 0%, rgba(0, 0, 0, 1) 100%);
        background: -o-linear-gradient(top, rgba(255, 255, 255, 0) 0%, rgba(0, 0, 0, 1) 100%);
        background: -ms-linear-gradient(top, rgba(255, 255, 255, 0) 0%, rgba(0, 0, 0, 1) 100%);
        background: -moz-linear-gradient(top, rgba(255, 255, 255, 0) 0%, rgba(0, 0, 0, 1) 100%);
        background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 0%, rgba(0, 0, 0, 1) 100%);
    }

    .pswp__custom-caption .photo-story-item-text {
        padding-top: 7px;
    }

    /* RTL language overrides */
    #page[dir="rtl"] .photo-story-index {
        /* float: right; */
    }

    #page[dir="rtl"] .photo-story-zoom {
        /* float: left; */
    }

    #page[dir="rtl"] .photo-story-item-text {
        direction: rtl;
    }

    #page[dir="rtl"] .photo-story-item-text-caption {
        text-align: left;
        padding-right: 0;
        padding-left: 10px;
    }

    #page[dir="rtl"] .pswp__caption__center {
        text-align: right;
    }

    #page[dir="rtl"] #primary .pswp {
        margin-left: 0;
    }

    .photo-story h2 {
        font-size: 15px;
        line-height: 24px;
        padding: 0px;
        margin: 0px;
    }

