content:
table {
        border: 1px solid black;
    }

.table-container {
        width: 100%;
        overflow-x: auto;
    }

    table {
        width: 100%;
        table-layout: fixed;
        border-collapse: collapse;
        font-family: Noto Sans, sans-serif;
    }

    th {
        background-color: #00205C;
        color: white;
        padding: 10px;
        text-align: center;
        font-size: 18px;
    }

    td {
        vertical-align: top;
        padding: 10px;
        border: 1px solid #ccc;
        word-wrap: break-word;
        overflow-wrap: break-word;
        white-space: normal;
    }

    .header-row td {
        background-color: #f26829;
        color: white;
        font-weight: bold;
        text-align: center;
    }

    .footnote {
        padding: 10px;
        border: 1px solid #f36f21;
        margin-top: 10px;
        font-size: 14px;
    }

    .bold {
        font-weight: bold;
    }

    col {
        width: 25%;
    }

    @media (max-width: 768px) {
        table {
            font-size: 14px;
        }

        th {
            font-size: 16px;
        }

        .footnote {
            font-size: 13px;
        }
    }
